// Parse ArXiv Atom/XML response
const data = $input.first().json.data;

// Extract entries using regex (more reliable than XML parser)
const entryMatches = data.match(/<entry>([\s\S]*?)<\/entry>/g) || [];

if (entryMatches.length === 0) {
  throw new Error('No papers found in ArXiv response');
}

const papers = entryMatches.map(entry => {
  // Helper to extract tag content
  const getTag = (tag, content = entry) => {
    const match = content.match(new RegExp(`<${tag}[^>]*>([\\s\\S]*?)<\\/${tag}>`));
    return match ? match[1].replace(/<[^>]+>/g, '').trim().replace(/\s+/g, ' ') : '';
  };

  const getId = () => {
    const id = getTag('id');
    return id.replace('http://arxiv.org/abs/', '');
  };

  const getAuthors = () => {
    const authorMatches = entry.match(/<author>([\s\S]*?)<\/author>/g) || [];
    return authorMatches.map(a => getTag('name', a)).filter(n => n).join(', ');
  };

  const getCategory = () => {
    const catMatch = entry.match(/<arxiv:primary_category[^>]*term="([^"]+)"/);
    return catMatch ? catMatch[1] : 'cs.AI';
  };

  return {
    id: getId(),
    title: getTag('title'),
    summary: getTag('summary'),
    published: getTag('published').split('T')[0],
    link: `https://arxiv.org/abs/${getId()}`,
    pdf_link: `https://arxiv.org/pdf/${getId()}.pdf`,
    authors: getAuthors(),
    category: getCategory()
  };
});

return papers.map(paper => ({ json: paper }));

