parameters.jsCode:
const input = $input.all();
let papers = [];

// Flatten all papers from the "data" arrays
input.forEach(i => {
  if (Array.isArray(i.json.data)) {
    papers = papers.concat(i.json.data);
  }
});

const totalPapers = papers.length;
const avgImpactScore = Math.round(papers.reduce((sum, p) => sum + (p.impact_score || 0), 0) / totalPapers);

// Category distribution
const categoryCount = {};
papers.forEach(p => {
  const cat = p.category || 'Other';
  categoryCount[cat] = (categoryCount[cat] || 0) + 1;
});

// Impact score distribution
const highImpact = papers.filter(p => (p.impact_score || 0) >= 8).length;
const mediumImpact = papers.filter(p => (p.impact_score || 0) >= 5 && (p.impact_score || 0) < 8).length;
const lowImpact = papers.filter(p => (p.impact_score || 0) < 5).length;

// Top papers
const topPapers = papers
  .sort((a, b) => (b.impact_score || 0) - (a.impact_score || 0))
  .slice(0, 3)
  .map(p => ({
    title: p.title,
    impactScore: p.impact_score,
    category: p.category,
    keyInsight: p.key_insight
  }));

// Category trends
const topCategories = Object.entries(categoryCount)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([category, count]) => ({ category, count }));

// Author analysis
const authorCount = {};
papers.forEach(p => {
  if (p.authors) {
    p.authors.split(',').map(a => a.trim()).forEach(author => {
      authorCount[author] = (authorCount[author] || 0) + 1;
    });
  }
});

const topAuthors = Object.entries(authorCount)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .map(([author, count]) => ({ author, count }));

// Trends and insights
const trends = [];
if (highImpact > totalPapers * 0.3) {
  trends.push(`üî• High activity: ${highImpact} high-impact papers (${Math.round(highImpact / totalPapers * 100)}%)`);
}
if (topCategories.length > 0) {
  trends.push(`üìà Trending: ${topCategories[0].category} leads with ${topCategories[0].count} papers`);
}
if (topAuthors.length > 0 && topAuthors[0].count > 1) {
  trends.push(`üë§ Prolific author: ${topAuthors[0].author} (${topAuthors[0].count} papers)`);
}

// Final output
return [{
  json: {
    summary: { totalPapers, avgImpactScore, highImpact, mediumImpact, lowImpact },
    categoryDistribution: categoryCount,
    topCategories,
    topPapers,
    topAuthors,
    trends,
    papers
  }
}];

// Generate advanced HTML email with trends, citations, and comprehensive statistics
